export function trimPostLength(text) {
  if (text.length > 300) {
    return text.slice(0, 300) + '...';
  }

  return text;
}
export function formatDate(dateString) {
  var dateObj = new Date(dateString);
  var dateYear = dateObj.getFullYear().toString().slice(2, 4);
  return "".concat(dateObj.getDate(), "/").concat(dateObj.getMonth() + 1, "/").concat(dateYear);
}
