var fetch = require('node-fetch');

var ApolloClient = require('apollo-client').ApolloClient;

var InMemoryCache = require('apollo-cache-inmemory').InMemoryCache;

var HttpLink = require('apollo-link-http').HttpLink;

var uri = "https://graphql.contentful.com/content/v1/spaces/".concat(process.env.CF_SPACE_ID, "/environments/").concat(process.env.CF_ENVIRONMENT);
var cache = new InMemoryCache();
var link = new HttpLink({
  uri: uri,
  fetch: fetch,
  headers: {
    Authorization: 'Bearer ' + process.env.CF_DELIVERY_ACCESS_TOKEN
  }
});
var client = new ApolloClient({
  cache: cache,
  link: link,
  defaultOptions: {
    query: {
      fetchPolicy: 'network-only',
      errorPolicy: 'all'
    }
  }
});
module.exports = client;
